changeSet.createProcedure:
ALTER PROCEDURE [dbo].[dsh_GetGroupItems] @Group NVARCHAR(100) AS
BEGIN IF @Group = 'Contact_name' BEGIN SELECT DISTINCT ec.id_contact AS id, ec.name AS name FROM ent_Contacts ec WHERE ec.isReport = 1 AND ec.isActiv = 1 END ELSE IF @Group = 'Good_name' BEGIN SELECT eg.id_good AS id, eg.name AS name FROM ent_Goods eg WHERE eg.isDeleted = 0 and eg.isGroup = 0 END
ELSE IF @Group = 'Contact_Group' BEGIN SELECT cg.id AS id, cg.[key] + ' ' + cg.name AS name FROM ent_ContactGroups cg WHERE cg.isDeleted = 0 END END;
